import Link from 'next/link';
import React from 'react';
import Layout from '../components/MyLayout';
import useStats from '../components/useStats';

export default function Index() {
  const { pending, error, stats } = useStats('https://covid19.mathdro.id/api/');
  return (
    <Layout>
      <p>Confirmed: {stats.confirmed.value}</p>
      <p>Recovered: {stats.recovered.value}</p>
      <p>Deaths: {stats.deaths.value}</p>
    </Layout>
  );
}
