import React from 'react';
import Loading from './Loading';

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
  },
  confirmed: {
    color: 'black',
    border: '1px solid red',
    padding: 10,
    width: '10rem',
  },
  recovered: {
    color: 'green',
  },
  death: {
    color: 'red',
  },
};
export default function Global({ stats, loading }) {
  if (loading) {
    return <Loading />;
  }

  return (
    <div style={styles.container}>
      <div style={styles.confirmed}>
        <p>Confirmed: {stats.confirmed.value}</p>
      </div>
      <div style={styles.recovered}>
        <p>Recovered: {stats.recovered.value}</p>
      </div>
      <div style={styles.death}>
        <p>Deaths: {stats.deaths.value}</p>
      </div>

      <style jsx>{`
        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
          font-family: 'Arial';
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </div>
  );
}
