import React from 'react';
import Loading from './Loading';

export default function Global({ stats, loading }) {
  if (loading) {
    return <Loading />;
  }

  return (
    <div className="container">
      <div className="box">
        <p className="text">Confirmed: {stats.confirmed.value}</p>
      </div>
      <div className="box">
        <p className="text">Recovered: {stats.recovered.value}</p>
      </div>
      <div className="box">
        <p className="text">Deaths: {stats.deaths.value}</p>
      </div>

      <style jsx>{`
        .container {
          display: 'flex',
          flexDirection: 'row',
        }
        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
          font-family: 'Arial';
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </div>
  );
}
