import React from 'react';
import { Statistic, Card, Row, Col } from 'antd';
import { ArrowUpOutlined, ArrowDownOutlined } from '@ant-design/icons';
import Loading from './Loading';

export default function Global({ stats, loading }) {
  if (loading) {
    return <Loading />;
  }

  return (
    <Row gutter={16}>
      <Col span={12}>
        <Card>
          <Statistic
            title="Confirmed"
            value={stats.confirmed.value}
            valueStyle={{ color: '#c38755' }}
            prefix={<ArrowUpOutlined />}
          />
        </Card>
      </Col>

      <Col span={12}>
        <Card>
          <Statistic
            title="Recovered"
            value={stats.recovered.value}
            valueStyle={{ color: '#3f8600' }}
            prefix={<ArrowUpOutlined />}
          />
        </Card>
      </Col>

      <Col span={12}>
        <Card>
          <Statistic
            title="Deaths"
            value={stats.deaths.value}
            valueStyle={{ color: '#cf1322' }}
            prefix={<ArrowUpOutlined />}
          />
        </Card>
      </Col>
    </Row>
  );

  return (
    <div className="container">
      <div className="box confirmed">
        <p className="text">Confirmed</p>
        <p className="value text">{stats.confirmed.value}</p>
      </div>
      <div className="box recovered">
        <p className="text">Recovered</p>
        <p className="value text">{stats.recovered.value}</p>
      </div>
      <div className="box deaths">
        <p className="text">Deaths</p>
        <p className="value text">{stats.deaths.value}</p>
      </div>

      <style jsx>{`
        .container {
          display: flex;
          justify-content: space-around;
        }

        .box {
          background-color: #ebf5f3cc;
          border-radius: 0.5em;
          padding: 1.5em;
          text-align: center;
          width: 15rem;
          align-items: center;
          vertical-align: middle;
        }

        .text {
          font-size: 1.5em;
        }

        .value {
          margin-top: 0.2em;
        }

        .confirmed {
          color: orange;
        }
        .recovered {
          color: green;
        }
        .deaths {
          color: red;
        }
      `}</style>
    </div>
  );
}
