import React from 'react';
import Loading from './Loading';

export default function Global({ stats, loading }) {
  if (loading) {
    return <Loading />;
  }

  return (
    <div className="container">
      <div className="box">
        <p className="text confirmed">Confirmed</p>
        <p className="value">{stats.confirmed.value}</p>
      </div>
      <div className="box">
        <p className="text recovered">Recovered</p>
        <p className="value">{stats.recovered.value}</p>
      </div>
      <div className="box">
        <p className="text deaths">Deaths</p>
        <p className="value">{stats.deaths.value}</p>
      </div>

      <style jsx>{`
        .container {
          display: flex;
          justify-content: space-around;
          // flex-direction: column;
        }

        .box {
          // border: 1px solid red;
          justify-content: center;
          justify-items: center;
          align-self: center;
          background-color: #ebf5f3cc;
          border-radius: 0.5em;
          padding: 1.5em;
          color: red;
        }

        .text {
          display: table-caption;
          font-size: 1.5em;
        }

        .confirmed {
          color: orange;
        }
        .recovered {
          color: green;
        }
        .deaths {
          color: red;
        }
      `}</style>
    </div>
  );
}
