import React from 'react';
import { Statistic, Card, Row, Col } from 'antd';
import { ArrowUpOutlined } from '@ant-design/icons';
import Loading from './Loading';
import useStats from './useStats';

export default function DailySummary() {
  const { loading, error, stats } = useStats(
    'https://covid19.mathdro.id/api/daily'
  );
  if (loading) {
    return <Loading />;
  }
  Object.entries(stats).map(day => {
    const dayNow = new Date().getDate();
    const month = new Date().getMonth() + 1;

    if (day[1].reportDateString === `2020/0${month}/${dayNow}`) {
      return (
        <Row gutter={32} style={{ textAlign: 'center' }}>
          <Col span={8}>
            <Card>
              <Statistic
                title="Confirmed"
                value={day[1].deltaConfirmed}
                valueStyle={{ color: '#c38755' }}
              />
            </Card>
          </Col>
          <Col span={8}>
            <Card>
              <Statistic
                title="Recovered"
                value={day[1].deltaRecovered}
                valueStyle={{ color: '#3f8600' }}
              />
            </Card>
          </Col>
          <Col span={8}>
            <Card>
              <Statistic
                title="Deaths"
                //   value={stats.deaths.value}
                valueStyle={{ color: '#cf1322' }}
              />
            </Card>
          </Col>
        </Row>
      );
    }
  });

  return <Loading />;
}
