import React from 'react';
import Loading from './Loading';

export default function Global({ stats, loading }) {
  if (loading) {
    return <Loading />;
  }

  return (
    <div className="container">
      <div className="box">
        <p className="text confirmed">Confirmed: {stats.confirmed.value}</p>
      </div>
      <div className="box">
        <p className="text recovered">Recovered: {stats.recovered.value}</p>
      </div>
      <div className="box">
        <p className="text deaths">Deaths: {stats.deaths.value}</p>
      </div>

      <style jsx>{`
        .container {
          display: 'flex';
          flex-direction: 'column';
        }

        .box {
          // border: 1px solid red;
          background-color: #ebf5f3cc;
          border-radius: 0.5em;
        }

        .text {
          display: table-caption;
        }

        .confirmed {
          color: orange;
        }
        .recovered {
          color: green;
        }
        .deaths {
          color: red;
        }
      `}</style>
    </div>
  );
}
